mapper.select:
		 select t.* 
                  from AppVersion t
                   where   verid=#{id,jdbcType=VARCHAR}

select * from (select aa.*,rownum rn from 
		(select t.*
			  from AppVersion t
			 order by updatetime desc
		) aa where
	rownum<=#{endNum,jdbcType=VARCHAR}  

  )where rn>#{startNum,jdbcType=VARCHAR}
